import osimport csvfrom glob import globfrom xlsxwriter.workbook import Workbookfrom pymongo import MongoClientclient = MongoClient('localhost')db=input("enter database : ")collection=input("enter collection : ")fields=input("list of fields attributes : ")filename=input("Enter file name : ")def mongodbtocsv(db,collection,fields,filename):    myfields=','.join(map(str, fields))    os.system('mongoexport --db '+db+' --collection '+collection+' --fields '+myfields+' --out '+filename+'.csv')def mongodbtojson(db,collection,filename):    os.system('mongoexport --db '+db+' --collection '+collection+' --pretty --out '+filename+'.json')def mongodbtoxls(db,collection,fields,filename):    myfields = ','.join(map(str, fields))    os.system('mongoexport --db ' + db + ' --collection ' + collection + ' --fields ' + myfields + ' --out ' + filename + '.csv')    for csvfile in glob(filename+'.csv'):        name =filename        workbook = Workbook(str(name) + '.xlsx', {'strings_to_numbers': True, 'constant_memory': True})        worksheet = workbook.add_worksheet()        with open(csvfile, 'r') as f:            r = csv.reader(f)            for row_index, row in enumerate(r):                for col_index, data in enumerate(row):                    worksheet.write(row_index, col_index, data)        workbook.close()print("Key associated with file extensions")print("csv => 1")print("json => 2")print("xls => 3")key=int(input("Enter key : "))if(key == 1):    mongodbtocsv(db,collection,fields,filename)elif(key == 2):    mongodbtojson(db,collection,filename)elif(key == 3):    mongodbtoxls(db,collection,fields,filename)else:    print("Invalid key")